templates.tableRow:
{% util.eachComponent(components, function(component) { %}
    {% if (!component.hasOwnProperty('tableView') || component.tableView) { %}
      <td class="editgrid-table-column">
        {{ getView(component, row[component.key]) }}
      </td>
    {% } %}
  {% }) %}
  {% if (!instance.options.readOnly && !instance.disabled) { %}
    <td class="editgrid-table-column">
      <div class="btn-group">
        <button class="btn btn-default btn-light btn-sm editRow" aria-label="{{ t('Edit row') }}"><i class="{{ iconClass('edit') }}"></i></button>
        {% if (!instance.hasRemoveButtons || instance.hasRemoveButtons()) { %}
        <button class="btn btn-danger btn-sm removeRow" aria-label="{{ t('Remove row') }}"><i class="{{ iconClass('trash') }}"></i></button>
        {% } %}
      </div>
    </td>
  {% } %}

actions.customAction:
// this block has to be executed only if the below mentioned fields are set with proper values
if(submission.data['worktype'] && submission.data['eaact'] && submission.data['startdate']) {
try{
  var work_type_id = data.worktype;
  var ea_act_id = data.eaact;
  var rows = [];
  fetch("http://localhost:5001/api/v1/codes/phase_codes/ea_acts/"+ea_act_id+"/work_types/"+work_type_id).then(response=>response.json()).then(data=>{
	  console.log('DATA', data);
	  var startdate = submission.data['startdate'];
	  if(data) {
		    data.forEach(function(phase,index){
			if(phase.legislated === true){
				startdate = moment(startdate).add(1,'days').format('YYYY-MM-DD');
			}
			var edate = moment(startdate).add(phase.duration,'days').format('YYYY-MM-DD')
		      rows.push({
		        workphase: phase.id,
		        phasename: phase.name,
				legislated: phase.legislated,
		        duration: phase.duration,
		        startdate: startdate,
		        enddate: edate
		      });
			  daysToBeAdded = phase.duration;
			  startdate = edate;
		    });
		    instance.setValue(rows);
		    console.log(form.submission);
  }else{
    value = [];
  }});

}catch(e){
	console.log('error',e.message);
}
}

