// this block has to be executed only if the below mentioned fields are set with proper values
var work_type_id = submission.data.works.work_type_id;
var ea_act_id = submission.data.works.ea_act_id;
var start_date = submission.data.works.start_date;
var hasValue = data.onload.hasValue;
var dateFormat = 'YYYY-MM-DD';
// var start_date_hidden = submission.data['start_date']
// applicationId empty means the form is being submitted for the first time. Only then the api call has to be initiated to fill the table
if (!hasValue(data.applicationId)
    && hasValue(work_type_id)
    && hasValue(ea_act_id)
    && hasValue(start_date)) {
    try {
        var phases = [];
        var events = [];
        var engagements = [];
        var baseUrl = data.reportapiurl;
        var headers = {
            headers: {
                Authorization: 'Bearer ' + localStorage.getItem('authToken')
            }
        }
        fetch(baseUrl + "phases/ea_acts/" + ea_act_id + "/work_types/" + work_type_id, headers)
            .then(response => response.json())
            .then(data => {
                if (data) {
                    data.forEach(function (phase, index) {
                        var edate = moment(start_date).add(phase.duration, 'days');
                        // filling in the phases grid
                        phases.push({
                            id: '',
                            phase_id: phase.id,
                            phasename: phase.name,
                            eaactlabel: phase.ea_act.name,
                            legislated: phase.legislated,
                            duration: phase.duration,
                            durationlabel: phase.duration,
                            start_date: start_date,
                            anticipated_end_date: edate
                        });
                        // milestones marked as EVENT as event_type and auto population turned on
                        var autoPopulatedEvents = phase.milestones.filter(p => p.kind === 'EVENT' && p.auto === true);
                        autoPopulatedEvents.forEach((event, event_index) => {
                            var event_start_date = moment(start_date).add(event.start_at, 'days');
                            var event_end_date = event_start_date.add(event.duration, 'days');
                            events.push({
                                id: '',
                                title: event.name,
                                allowEditing: true,
                                phaselabel: phase.name,
                                eaactlabel: phase.ea_act.name,
                                milestonelabel: event.name, // same as that of milestone
                                anticipated_start_date: moment(event_start_date),//.format(dateFormat),
                                anticipated_end_date: moment(event_end_date),//.format(dateFormat),
                                milestone_id: event.id,
                                milestone_type_name: event.milestone_type.name,
                                isfixedevent: true,
                                isendevent: (event_index === autoPopulatedEvents.length - 1),
                                phaseindex: index,
                                duration: event.duration,
                                index: events.length
                            });
                        })
                        debugger
                        // milestones marked as ENGAGEMENT as event_type and auto population turned on 
                        var autoPopulatedEngagements = phase.milestones.filter(p => p.kind === 'ENGAGEMENT' && p.auto === true);
                        autoPopulatedEngagements.forEach((engagement, eng_index) => {
                            var engagement_start_date = moment(start_date).add(engagement.start_at, 'days');
                            var engagement_end_date = moment(engagement_start_date).add(engagement.duration, 'days');
                            engagements.push({
                                phase_label: phase.name,
                                title: engagement.name,
                                start_date: moment(engagement_start_date),//.format(dateFormat),
                                end_date: moment(engagement_end_date),//.format(dateFormat),
                                id: '',
                                is_valid: true,
                                phaseindex: index,
                                correlationkey: ['PECP Coming','PECP','PECP Open','PECP Closing'].includes(engagement.milestone_type.name)?'default':'',
                                duration: engagement.duration,
                                milestone_type_name: engagement.milestone_type.name,
                                start_at: engagement.start_at,
                                "work_engagements": {
                                    phase_id: phase.id,
                                    id: '',
                                    project_id: submission.data.works.project_id,
                                    work_type_id: submission.data.works.work_type_id,
                                    milestone_id: engagement.id,
                                    engagement_id: ''
                                }
                            });
                        })
                        daysToBeAdded = phase.duration;
                        start_date = moment(edate).add(1, 'days');
                    });
                    instance.setValue(phases);
                    var eventsGrid = utils.getComponent(instance.parent.components, 'works-events');
                    var engagementsGrid = utils.getComponent(instance.parent.components, 'works-engagements');
                     var anticipated_decision_date;
                    try {
                        
                        utils.findComponent(instance.root.components, 'works.anticipated_decision_date', ['works.anticipated_decision_date'], (components) => {
                            anticipated_decision_date = components
                        })
