                    anticipated_decision_date.setValue(phases[phases.length - 1].anticipated_end_date);
                    submission.data['works-events'] = events;
                    submission.data.engagements = engagements;
                    eventsGrid.setValue(events);
                    engagementsGrid.setValue(engagements);
                    instance.emit('onEventsGridChanged', {});
                } else {
                    value = [];
