var component = result[0].component;
if(component && component.key === 'works-events') {
 try {    
    const rebindGrids = () => {
        var eventsGrid = utils.getComponent(instance.parent.components, 'works-events');
        var phaseGrid = utils.getComponent(instance.parent.components, 'works-work_phases');
        var anticipated_decision_date;
        try {

            utils.findComponent(instance.root.components, 'works.anticipated_decision_date', ['works.anticipated_decision_date'], (components) => {
                anticipated_decision_date = components
            })
        } catch (e) { console.error(e.message) };
        anticipated_decision_date.setValue(phases[phases.length - 1].anticipated_end_date);
        
        setTimeout(function () {
        eventsGrid.setValue(events);
        phaseGrid.setValue(phases); 
        }, 100);
        instance.emit('eventsGridChanged', {});
    }
    const isStartEvent = (e) => {
        var filteredEvents = events.filter(p => p.phaseindex === e.phaseindex);
        return e.index === filteredEvents[0].index;
    }
    const isEndEvent = (e) => {
        var filteredEvents = events.filter(p => p.phaseindex === e.phaseindex);
        var doesEventDatesPushOut = e.index === filteredEvents[filteredEvents.length - 1].index;
        if(['Extension','Suspension'].includes(e.milestone_type_name)) {
            doesEventDatesPushOut = true;
        }
        return doesEventDatesPushOut;
    }
    const cancelEditActualDates = (newEvent) =>{
        var date_ctrl = newEvent.end_date && newEvent.end_date !== "" ? 'start_date' : 'anticipated_start_date';
        var end_date = date_ctrl.replace('start_date', 'end_date');
        events[newEvent.index][date_ctrl] = moment(newEvent[date_ctrl]).add((diff * -1), 'days');//.format(dateFormat);
        events[newEvent.index][end_date] = events[newEvent.index][date_ctrl]
        rebindGrids();
    }
    const calculateDateChanges = (newEvent) => {
        var eventPhase = phases[newEvent.phaseindex];
        const dateToCheck = (e) => (e.end_date && e.end_date != "") ? e.end_date : e.anticipated_end_date;
        var date_to_check = moment(dateToCheck(newEvent));
        var diff = date_to_check.startOf('day').diff(moment(isStartEvent(newEvent) ? eventPhase.start_date : eventPhase.anticipated_end_date).startOf('day'), 'days');
        var alertDiaglogText = `Actual End Date (${date_to_check.format('YYYY-MM-DD')}) falls outside the start/end dates of the selected phase(${moment(eventPhase.start_date)} to ${moment(eventPhase.anticipated_end_date)}). Do you want to continue?`;
        if(diff > 0 && (!isStartEvent(newEvent) && !isEndEvent(newEvent))) {
            if(!confirm(alertDiaglogText)){
                cancelEditActualDates(newEvent);
                return value;
            }
        }
        else if (diff !== 0 && (isStartEvent(newEvent) || isEndEvent(newEvent))) {
            if (confirm(alertDiaglogText)) {
                    var phase_start_date;
                    phases.forEach((phase, phaseIndex) => {
                        if (phaseIndex >= newEvent.phaseindex) {
                            var eventsPerPhase = events.filter(e => e.phaseindex === phaseIndex);
                            eventsPerPhase.forEach((event) => {
                                if (event.index > newEvent.index) {
                                    var eventDiff = moment(event.anticipated_end_date).diff(moment(event.anticipated_start_date), 'days');
                                    event.anticipated_start_date = moment(event.anticipated_start_date).add(diff, 'days');//.format(dateFormat);
                                    event.anticipated_end_date = moment(event.anticipated_start_date).add(eventDiff, 'days');//.format(dateFormat);
    
                                    if (event.end_date && event.end_date != "" && event.start_date && event.start_date != "") {
                                        var actualDiff = moment(event.end_date).diff(moment(event.start_date), 'days');
                                        event.start_date = moment(event.start_date).add(diff, 'days');//.format(dateFormat);
                                        event.end_date = moment(event.start_date).add(actualDiff, 'days');//.format(dateFormat);
                                    }
                                }
                            });
                            var firstEvent = eventsPerPhase[0];
                            if (phaseIndex === newEvent.phaseindex) {
                                phase.diff = (phase.diff ? phase.diff : 0);
                                if (isEndEvent(newEvent)) {
                                    phase.diff += diff;
                                }
                                if (isEndEvent(newEvent)) {
                                    if (phase.legislated) {
                                        if (phase.diff !== 0) phase.durationlabel = phase.duration + '(' + (phase.diff > 0 ? '+' : '-') + (Math.abs(phase.diff)) + ')';
                                        else phase.durationlabel = phase.duration;
                                    } else {
                                        phase.durationlabel = (phase.duration + phase.diff)
                                    }
                                }
                                phase.start_date = moment(firstEvent.start_date && firstEvent.start_date != "" ? firstEvent.start_date : firstEvent.anticipated_start_date);//.format(dateFormat);
                                phase.anticipated_end_date = moment(phase.anticipated_end_date).add(diff, 'days');//.format(dateFormat);
                            } else {
                                phase.start_date = phase_start_date;
                                phase.anticipated_end_date = moment(phase_start_date).add(phase.duration, 'days');//.format(dateFormat);
                            }
                            phase_start_date = moment(phase.anticipated_end_date).add(1, 'days');//.format(dateFormat);
                        }
                    });
                instance.emit('phaseDatesChanged',{phaseindex: newEvent.phaseindex,datediff: diff,isStartEvent:isStartEvent(newEvent)});
                
            } else {
                cancelEditActualDates(newEvent);
                return value;
            }
        }
    }
    const generateRandomKey = () => {
        var text = "";
        var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

        for (var i = 0; i < 5; i++)
            text += possible.charAt(Math.floor(Math.random() * possible.length));

        return text;
    }
    const getMilestones = (phase_id,newEvent) => {
        try {
            var headers = {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem('authToken')
                }
            }
            return new Promise((resolve, reject) => {
                fetch(data.reportapiurl + 'milestones/phases/' + phase_id, headers)
                    .then(response => response.json())
                    .then(data => {
                        resolve({data,newEvent,rebindGrids});
                    })
                    .catch(err => reject(err))
            });
        } catch (e) {
            console.log(e.message);
        }
    }
    const formatEventDates = (newEvent) => {
        // formatting the dates
        newEvent.anticipated_start_date = moment(newEvent.anticipated_start_date);//.format(dateFormat);
        newEvent.anticipated_end_date = moment(newEvent.anticipated_end_date);//.format(dateFormat);
        if (newEvent.start_date && newEvent.start_date !== "") {
            newEvent.start_date = moment(newEvent.start_date);//.format(dateFormat);
        }
        if (newEvent.end_date && newEvent.end_date !== "") {
            newEvent.end_date = moment(newEvent.end_date);//.format(dateFormat);
        }
        if (newEvent.is_complete && newEvent.is_complete === true) {
            return value;
        }
    }



    
    var dateFormat = 'YYYY-MM-DD';
    var newEvent = result[0].row;
    // if the saved event is already completed, no need to run throught the logic
    if(data.applicationId==="" ||  (newEvent.is_complete && newEvent.is_complete === true)) {
        alert('Invalid operation');
        return value;
    }
    formatEventDates(newEvent);
    var events = submission.data['works-events'];
    var phases = submission.data['works-work_phases'];
    var insertIndex = newEvent.index;
    if (newEvent.phaseindex === undefined) {
        // inserting new object to the right place
        insertIndex = events.findIndex(p => p.phaseindex === data.activephase && p.isendevent === true);
        newEvent.phaseindex = data.activephase;
        events.splice(insertIndex, 0, newEvent);
        events.splice(events.length - 1, 1);
        if(newEvent.milestone_type_name === 'PECP') {
            events[insertIndex].correlationkey = generateRandomKey();
            getMilestones(phases[newEvent.phaseindex].phase_id,events[insertIndex]).then(result=>{
                var newEvent = result.newEvent;
                var phases = submission.data['works-work_phases'];
                var phase = phases[newEvent.phaseindex];
                var pecpCloseMilestone = result.data.filter(p=>p.name === 'PECP Close' && p.kind === 'EVENT')[0];
                var pecpCloseEvent = {
                    id: '',
                    title: pecpCloseMilestone.name,
                    allowEditing: false,
                    phaselabel: phase.name,
                    eaactlabel: phase.eaactlabel,
                    milestonelabel: pecpCloseMilestone.name, // same as that of milestone
                    anticipated_start_date: newEvent.anticipated_end_date, //pecp close will at the end of pecp
                    anticipated_end_date: newEvent.anticipated_end_date,
                    start_date: newEvent.end_date,
                    end_date: newEvent.end_date,
                    milestone_id: pecpCloseMilestone.id,
                    milestone_type_name: pecpCloseMilestone.milestone_type.name,
                    isfixedevent: true,
                    isendevent: false,
                    phaseindex: newEvent.phaseindex,
                    duration: newEvent.number_of_days,
                    index: events.length,
                    correlationkey: newEvent.correlationkey,
                    never_edit: true
                }
                events.splice(newEvent.index+1, 0, pecpCloseEvent);
                events.splice(events.length - 1, 1);
                events.forEach((event, index) => event.index = index);
                result.rebindGrids();
                if(newEvent.milestone_type_name === 'PECP') {
                    var pecpClose = events[newEvent.index + 1]
                    instance.emit('pecpEventUpdated',{pecp: newEvent,pecpClose,op:'insert'});
                }
            });
        }         
        // recalculating the index
        events.forEach((event, index) => event.index = index);
    }else{
        if(newEvent.milestone_type_name === 'PECP') {
            var pecpCloseEvent = events.filter(p=>p.correlationkey === newEvent.correlationkey && p.index !== newEvent.index)[0];
            pecpCloseEvent.anticipated_start_date = newEvent.anticipated_end_date; //pecp close will at the end of pecp
            pecpCloseEvent.anticipated_end_date = newEvent.anticipated_end_date;
            pecpCloseEvent.start_date = newEvent.end_date;
            pecpCloseEvent.end_date = newEvent.end_date;
            var pecpClose = events[newEvent.index + 1]
            instance.emit('pecpEventUpdated',{pecp: newEvent,pecpClose,op:'update'});
        }
    }
    if (newEvent.end_date && newEvent.end_date !== "") {
        if (newEvent.index !== 0) {
            var prevEvent = events[newEvent.index - 1];
            if (prevEvent.is_complete !== true) {
                alert('Previous event is not yet completed. You cannot proceed unless you complete the previous event');
                newEvent.start_date = "";
                newEvent.end_date = "";
                rebindGrids();
                return value;
            }
        }
        if (confirm('Event will be locked for future changes. Are you sure you want to proceed?')) {
            events[newEvent.index].is_complete = true;
            // if the new event which is marked as completed is PECP, then the corresponding PECP Close 
            // also has to be marked completed
            if (events[newEvent.index].milestone_type_name === 'PECP') {
                events[newEvent.index + 1].is_complete = true;
            }
            var phaseEvents = events.filter(p => p.phaseindex === newEvent.phaseindex);
            if (newEvent.index === phaseEvents[phaseEvents.length - 1].index) {
                submission.data.actionType = newEvent.phaseindex + 1;
                submission.data.activephase = newEvent.phaseindex + 1;
                submission.data.works.current_phase_id = phases[newEvent.phaseindex + 1].phase_id;
                submission.data.works.current_phase_name = phases[newEvent.phaseindex + 1].phasename;
            }
            data.completedEventPhase = phases[newEvent.phaseindex].phasename;
            data.completedEvent = newEvent.title;
        }
    }

    // allow editing 
    events.forEach((e) =>e.allowEditing = !((e.end_date != "" && e.is_complete === true) || e.never_edit));
    calculateDateChanges(newEvent);


    // check to see if there are termination decisions have been chosen
    var prevDiff = 0;
    var outcomes = data.onload.outcomes;
    for (var index = 0; index < events.length; index++) {
        var event = events[index];
