class EngagementCalculator {
    constructor() {
        this.eventArgs = result[0];
        this.dateFormat = 'YYYY-MM-DD';
        this.phases = data['works-work_phases'];
        this.events = data['works-events'];
        this.engagements = data['works-engagements'];
        this.phaseIndex = this.eventArgs.pecp ? this.eventArgs.pecp.phaseindex : this.eventArgs.pecpClose.phaseindex;
        this.correlationKey = this.eventArgs.pecp ? this.eventArgs.pecp.correlationkey : this.eventArgs.pecpClose.correlationkey;
        this.MILESTONE_TYPES = ['PECP', 'PECP Coming', 'PECP Open', 'PECP Closing'];
        this.relatedPECP = ['insert', 'update'].includes(this.eventArgs.op) ? this.eventArgs.pecp : (this.events.filter(p => p.correlationkey === this.eventArgs.pecpClose.correlationkey)[0]);
        this.instance = instance;
        this.milestones = this.engagements.filter(p => p.phaseindex === this.phaseIndex
            && this.MILESTONE_TYPES.includes(p.milestone_type_name)).map(p => {
                return {
                    milestone_type_name: p.milestone_type_name,
                    milestone_id: p.work_engagements.milestone_id,
                    title: p.title
                }
            });
    }

    process() {
        const self = this;
        if (self.eventArgs.op === 'delete') {
            var deleteIndex = self.engagements.findIndex(p => p.correlationkey === self.correlationKey);
            [1, 2, 3, 4].forEach(i => self.engagements.splice(deleteIndex, 1));
            self.bindGrid();
        } else {
            if(!this.milestones || this.milestones.length < 4) {
            self.getMilestones((self.phases[self.phaseIndex]).phase_id).then(result => {
                self.milestones = result.data.filter(p => p.kind === 'ENGAGEMENT' && self.MILESTONE_TYPES.includes(p.milestone_type.name))
                    .map(p => {
                        return {
                            milestone_type_name: p.milestone_type.name,
                            milestone_id: p.id,
                            title: p.name
                        }
                    })
                result.self.engagementDateCalculation();
            })
        }else{
            self.engagementDateCalculation();
        }
        }
    }
    getMilestones(phase_id) {
        const self = this;
        try {
            var headers = {
                headers: {
                    Authorization: 'Bearer ' + localStorage.getItem('authToken')
                }
            }
            return new Promise((resolve, reject) => {
                fetch(data.reportapiurl + 'milestones/phases/' + phase_id, headers)
                    .then(response => response.json())
                    .then(data => {
                        resolve({ data, self });
                    })
                    .catch(err => reject(err))
            });
        } catch (e) {
            console.log(e.message);
        }
    }
    engagementDateCalculation() {
        const self = this;
        var pecpEngagementToUpdate;
        var pecpEngagementToUpdateIndex = -1;
        var phaseFilteredEngagements = self.engagements.filter(p => p.phaseindex === self.phaseIndex
            && p.milestone_type_name === self.MILESTONE_TYPES[0]);

        if (self.eventArgs.op === 'insert') {
            // In case if first PECP added to a phase, the corresponding engagement will have correlationkey 'default'
            // then we change it to the correlationkey of the newly added event PECP
            pecpEngagementToUpdate = phaseFilteredEngagements.filter(p => p.correlationkey === 'default')[0];
            if (pecpEngagementToUpdate) pecpEngagementToUpdate.correlationkey = self.relatedPECP.correlationkey;
        }
        pecpEngagementToUpdate = self.createPECPEngagement(self.MILESTONE_TYPES[0], 0, self.relatedPECP.number_of_days);
        pecpEngagementToUpdateIndex = self.engagements.findIndex(p => p.correlationkey === pecpEngagementToUpdate.correlationkey
            && p.milestone_type_name === self.MILESTONE_TYPES[0]);
        var pecpComingEngagement = self.createPECPEngagement(self.MILESTONE_TYPES[1], -7, 0);
        var pecpOpenEngagement = self.createPECPEngagement(self.MILESTONE_TYPES[2], 0, 0);
        var pecpClosingEngagement = self.createPECPEngagement(self.MILESTONE_TYPES[3], self.relatedPECP.number_of_days - 2, 0);
        var indexOffset = -1;
        var deleteCount = 1;
        if (pecpEngagementToUpdateIndex === -1) {
            self.engagements.forEach((engagement, index) => {
                if (engagement.phaseindex === self.phaseIndex && engagement.milestone_type_name === self.MILESTONE_TYPES[0]) {
                    pecpEngagementToUpdateIndex = index;
                }
            });
            if(pecpEngagementToUpdateIndex !== -1) {
                indexOffset = 3;
                deleteCount = 0;
            }else {
                pecpEngagementToUpdateIndex = 2;
                deleteCount = 0;
            }
        }
        [pecpComingEngagement, pecpEngagementToUpdate, pecpOpenEngagement, pecpClosingEngagement]
            .forEach(pecp => {
                self.engagements.splice(pecpEngagementToUpdateIndex + (indexOffset++), deleteCount, pecp)
            });
        self.bindGrid();
    }
    createPECPEngagement(milestoneType, dateOffset, duration, args) {
        const self = this;
        var newPECPStartDate = moment(self.relatedPECP.start_date != "" ? self.relatedPECP.start_date : self.relatedPECP.anticipated_start_date).add(dateOffset, 'days');
        var milestone = self.milestones.filter(p => p.milestone_type_name === milestoneType)[0];
        var currentPhase = self.phases[self.phaseIndex];
        var pecpEng = {
            phase_label: currentPhase.phasename,
            title: milestoneType === self.MILESTONE_TYPES[0] ? self.relatedPECP.title : milestone.title,
            start_date: moment(newPECPStartDate),//.format(self.dateFormat),
            end_date: moment(newPECPStartDate).add(duration, 'days'),//.format(self.dateFormat),
            id: '',
            phaseindex: self.phaseIndex,
            duration,
            correlationkey: self.relatedPECP.correlationkey,
            milestone_type_name: milestoneType,
            "work_engagements": {
                phase_id: currentPhase.phase_id,
                id: '',
                project_id: submission.data.works.project_id,
                work_type_id: submission.data.works.work_type_id,
                engagement_id: '',
                milestone_id: milestone.milestone_id
            }
        }
        return pecpEng;
    }
    bindGrid() {
        const self = this;
        var engagementGrid = utils.getComponent(self.instance.parent.components, 'works-engagements');
        engagementGrid.resetValue();       
        setTimeout(function () {
            engagementGrid.setValue(self.engagements);
        }, 100);
    }

}

