function validate(data) {
  if(data.projects.name !== '') {
    var headers = {
    headers: {
    Authorization: 'Bearer '+localStorage.getItem('authToken')
    }
  }

  var url = localStorage.getItem('customApiUrl').split(',')[0];

  fetch(
    url+'projects/exists?name='+data.projects.name+getIdParameter(),headers
    )
    .then(response=>response.json())
    .then(res=>callback(data,res));
}
function callback(data,res) {
  data.validations = data.validations || {};
  data.validations.project_exists = res.exists;
}
function getIdParameter() {
  if(data.projects.id!=='') {
    return '&id='+data.projects.id;
  }
  return '';
}
